import React, { Component } from 'react';
import './styling.css';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import interactionPlugin from "@fullcalendar/interaction";
import bootstrapPlugin from '@fullcalendar/bootstrap';
import PopUp from './popup';
import { faCoffee, faSearch } from '@fortawesome/free-solid-svg-icons';
import Dialog from 'react-bootstrap-dialog'
import RenderBody from './renderBody';
import ChangeDate from './changeDate';

import {
  Button,
//  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle
} from "@material-ui/core";
import {getEvents} from "../eventData";
import {getCategories} from "../categoriesData";
export default class TheCalendar extends React.Component {
  constructor(props) {
    super(props);

    this.replaceModalItem = this.replaceModalItem.bind(this);
    this.saveModalDetails = this.saveModalDetails.bind(this);
    this.handleClick = this.handleClick.bind(this)
    
  }
  state = {
    addModalChangeShow: false,
    addModalShow: false,
    modal: false,
    requiredItem: 0,
    events:[],
    categories:[],
    event:{title: 'my event',start: '2020-04-01', end:'2020-04-02'},
    seen: false
  }
  replaceModalItem(info) {
    this.setState({
      requiredItem: this.state.events.indexOf(info.event)
    });
  }

  saveModalDetails(item) {
    const requiredItem = this.state.requiredItem;
    let tempevents = this.state.events;
    tempevents[requiredItem] = item;
    this.setState({ events: tempevents });
  }

  deleteItem(index) {
    let tempevents = this.state.events;
    tempevents.splice(index, 1);
    this.setState({ events: tempevents });
  }
    
  componentDidMount(){
    this.setState({events: getEvents(), categories: getCategories()});
  }

    handleDateClick = (arg) => { // bind with an arrow function
       
      }
      formatEvents() {
        return this.state.events.map(event => {
                  const {title, end, start} = event
      
                  let startTime = new Date(start)
                  let endTime = new Date(end)
      
                  return {
                    title, 
                    start: startTime,
                    end: endTime, 
                    extendedProps: {...event}
                  }
              })
      }
     handleClick=(eventClickInfo)=>{
        
        this.dialog.show({
           // title:" title",
            body: 'You want to add an event ?',
         
           
            actions: [
              Dialog.CancelAction(),
             
              Dialog.DefaultAction(
                'Yes',
                () => {
                  this.setState({addModalShow:true})},
                'btn-primary'
              )
            ],
            bsSize: 'small',
            onHide: (dialog) => {
              dialog.hide()
              console.log('closed by clicking background.')
            }
          })

      }
      toggle = () => {
        this.setState({ modal: !this.state.modal });
      };
    
      handleDragStop = () => {
        this.dialog.show({
          // title:" title",
           body: 'The date will be changed ',
        
          
           actions: [
             Dialog.CancelAction(),
            
             Dialog.DefaultAction(
               'OK',
               () => {
                 console.log('do stuff')},
               'btn-primary'
             )
           ],
           bsSize: 'small',
           onHide: (dialog) => {
             dialog.hide()
             console.log('closed by clicking background.')
           }
         })
      };
      // handleEventClick= ({event}) => {
        // openevent is a function I wrote to open a form to edit that appointment
       // this.props.openAppointment(event.extendedProps)
 //this.dialog.showAlert('Hello Dialog!')}
 
  /*  handleEventDrop = (info) => {
            if(window.confirm("Are you sure you want to change the event date?")){
                console.log('change confirmed')
    
                // updateAppointment is another custom method
                this.props.updateAppointment({...info.event.extendedProps, start: info.event.start, end: info.event.end})
    
            } else {
                console.log('change aborted')
            }
       }*/
       changeTitle = () => {   
        let events = [...this.state.events];
        events[0].title = new Date().toTimeString();
        events[1].title = new Date().toTimeString();
    
        this.setState({  events });
      };
      handleDrop=()=>{
        let events = [...this.state.events];
       
      }
      formatDate(string){
        var options = { year: 'numeric', month: 'long', day: 'numeric' };
        return new Date(string).toLocaleDateString([],options);
    }
      
  render() {
    let {title, start, end, events}=this.state;
            var dateFormat = require('dateformat');
    let addModalClose =()=>{
      this.setState({addModalShow:false});
    }
    let changeStateEvents=()=>{
      this.setState({events});
      console.log(events.filter(e => e.title =="Aeroday" ));
    }
    let addModalChangeClose =()=>{
      this.setState({addModalShow:false})
    }

    const requiredItem = this.state.requiredItem;
    let modalData = this.state.events[requiredItem];
    let handle =()=>{
      this.setState({addModalShow:true})
    }
    let handleSec =()=>{
      this.setState({addModalChangeShow:true})
    }
    return (
    <div className='jumbotron'>

              <FullCalendar dateClick={this.handleDateClick} 
              plugins={[ dayGridPlugin, interactionPlugin,bootstrapPlugin ]}
               
                editable={true}
                selectable={true}
                selectMirror={true}
               // eventClick = {function(calEvent, jsEvent, view, resourceObj) {alert(calEvent.title)}}
               // eventDragStop={this.handleDragStop}
                eventClick={this.changeTitle}
                eventDrop={function(info) {
                 alert(info.event.title + " was dropped on " +  info.event.start.toISOString().split('T')[0]);
                //  LocalDate.parse(info.event.start.toISOString(), DateTimeFormatter.ISO_OFFSET_DATE_TIME));
                 let newStart= info.event.start.toISOString().split('T')[0];
               //  let events = [...this.state.events];
                
                (events.filter(e => e.title ==info.event.title )).start= newStart;
              console.log(events.filter(e => e.title ==info.event.title ));
              
             handleSec();
               
                   
                }}
                select={this.handleClick}
                dateClick={this.handleClick}
                events={this.formatEvents()}
                slotWidth={60}
                eventColor= '#378006'
                eventBackgroundColor='white'
                eventBorderColor="black"
                eventTextColor='red'
                customButtons={{
                  myCustomButton: {
                      text: 'Add new event ',
                      click:handle
                      },
                  }
             }
              header={{
                  right:  'prev,next today ',
                  center: 'title',
                  left:'myCustomButton,timeGridWeek,timeGridDay,listWeek'
              }}
             
                
              />
             
              <Dialog className="modal-dialog modal-dialog-centered"
              
              ref={(component) => { this.dialog = component }}

              OKAction={handle}
               >
                 <Button>hello</Button>
               </Dialog>
                 <RenderBody
                 
                 show={this.state.addModalShow}
                 onHide={addModalClose}/>
                 
                 <ChangeDate
               show={this.state.addModalChangeShow}
               onChange={changeStateEvents}
               onHide={addModalChangeClose}
               />
      
              
              </div>


)
  }
}
