import gauche from '../assets/gauche.png';
import droite from '../assets/droite.png';
import img2 from '../assets/bright_insat.png';
import Slider from 'react-slick';
import {getClubs} from "../datafolder/clubData";
import './styling.css';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import React,{Component, useState,useEffect} from "react";
import "./styling.css";
import RBCarousel from "react-bootstrap-carousel";
import "react-bootstrap-carousel/dist/react-bootstrap-carousel.css";
import { Scroller } from 'fullcalendar';

const styles = { height: 400, width: "100%" };
const icon_glass = <span className="fa fa-glass" />;
const icon_music = <span className="fa fa-music" />;

class ClubSlider extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        clubs:getClubs(),
      autoplay: true,
      clicked: false
    
    
    };
  }

 
  slider=()=>{
    var slideshowImages = document.querySelectorAll(".intro.slideshow-img:first-child")
    var delay = 3000;
    let counter = 0;

    slideshowImages[counter].style.display ="block";
    setInterval(function(){
      slideshowImages[counter].style.display='none';
      counter = (counter)+1 % slideshowImages.length;
      slideshowImages[counter].style.display ="block";
  
    }, delay);
  
  }

  
  onSelect = (active, direction) => {
    console.log(`active=${active} && direction=${direction}`);
  };
  visiableOnSelect = active => {
    console.log(`visiable onSelect active=${active}`);
  };
  slideNext = () => {
    this.slider.slideNext();
  };
  slidePrev = () => {
    this.slider.slidePrev();
  };
  goToSlide = () => {
    this.slider.goToSlide(1);
  };
  autoplay = () => {
    this.setState({ autoplay: !this.state.autoplay });
  };
  _changeIcon = () => {
    let { leftIcon, rightIcon } = this.state;
    leftIcon = leftIcon ? undefined : icon_glass;
    rightIcon = rightIcon ? undefined : icon_music;
    this.setState({ leftIcon, rightIcon });
  };

  groupIntoThrees (children) {
    const output = []
    let currentGroup = []
  
    children.forEach((child, index) => {
      currentGroup.push(child)
  
      if (index % 3 === 2) {
        output.push(currentGroup)
        currentGroup = []
      }
    })
  
    return output
  }
   
 arrow_click=()=>{
   this.setState ({clicked : true});
   console.log('state of clickked '+this.state.clicked);
 }
 
  render() {
    let {clubs, slideIndex,clicked}=this.state;
    const length=clubs.length;
    var clubs_bloc =[];
    var i = 1;
    var test = false;
    var interval = 3000;
    function testing (){
     var bloc =[];
     
      console.log('entered')
     if (i<length-3){
      bloc = clubs.slice(i, i+3);
      i = i+1}
      else{
        let d = 3-(length -i );
        bloc = clubs.slice (i, length) ;
        
        i=0;
        bloc = bloc.concat(clubs.slice(i, d));
        i=d;
        
    
      }
      document.getElementById('club_1').style.background ='url('+bloc[0].image+')';
      document.getElementById('club_2').style.backgroundImage =`url(${bloc[1].image})`;
      document.getElementById('club_3').style.backgroundImage =`url(${bloc[2].image})`;
      console.log('testing end')

    }

    setInterval(function(){
   
    if (test){
      i= i+1;
      test = false;
    }
     console.log('i afteeerr '+i);
      if (i<length-3){
      clubs_bloc = clubs.slice(i, i+3);
      i = i+1}
      else{
        let d = 3-(length -i );
        clubs_bloc = clubs.slice (i, length) ;
        
        i=0;
        clubs_bloc = clubs_bloc.concat(clubs.slice(i, d));
        i=d;
        
    
      }
      document.getElementById('club_1').style.background ='url('+clubs_bloc[0].image+')';
      document.getElementById('club_2').style.backgroundImage =`url(${clubs_bloc[1].image})`;
      document.getElementById('club_3').style.backgroundImage =`url(${clubs_bloc[2].image})`;

    },interval)
    

  var settings={
    autoplay: true,
    className: "slider variable-width",
    speed: 2000,
    autoplaySpeed: 2000,
    infinite: true,
    dots:false,
    arrows: true,
    speed:300,
    slidesToShow:7,
    slidesToScroll:3,
    responsive: [
    
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 2,
          initialSlide: 2
        }
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1
        }
      }
    ]
  }

    return (

        <React.Fragment>     
         <div className="row"  style={{ height:'50vh', marginTop:'80px',marginBottom:'50px'}} id='sliders'>
         <div className='col-md-1 arrowIcon' ><img src={gauche}  /></div>
          <div className='col-md-3 slideCol' id='club_1' style={{backgroundImage:'url('+clubs[0].image+')'}} ></div>
          <div className='col-md-3 slideCol' id='club_2' style={{backgroundImage:'url('+clubs[1].image+')'}}></div>
          <div className='col-md-3 slideCol' id='club_3' style={{backgroundImage:'url('+clubs[2].image+')'}}></div>
          <div className='col-md-1 arrowIcon' ><img src={droite} onClick= {testing}/></div>
          </div>
         
                      
           
       
         
             
  </React.Fragment>
)

  
 

    
}
}
export default ClubSlider;
