import img2 from '../assets/b.jpg';
import img1 from '../assets/c.jpg';
import img3 from '../assets/insat.png';
import {getClubs} from "../datafolder/clubData";
import './styling.css';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import Slider from "react-slick";
import React,{Component, useState,useEffect} from "react";
import "./styling.css";
import RBCarousel from "react-bootstrap-carousel";
import { Row, Col, Button, ButtonGroup } from "./bootstrap-component.jsx";
import "react-bootstrap-carousel/dist/react-bootstrap-carousel.css";
//import { Slider } from 'shards-react';

const styles = { height: 400, width: "100%" };
const icon_glass = <span className="fa fa-glass" />;
const icon_music = <span className="fa fa-music" />;

class ClubSlider extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        clubs:getClubs(),
      autoplay: true
    };
  }
  
  onSelect = (active, direction) => {
    console.log(`active=${active} && direction=${direction}`);
  };
  visiableOnSelect = active => {
    console.log(`visiable onSelect active=${active}`);
  };
  slideNext = () => {
    this.slider.slideNext();
  };
  slidePrev = () => {
    this.slider.slidePrev();
  };
  goToSlide = () => {
    this.slider.goToSlide(1);
  };
  autoplay = () => {
    this.setState({ autoplay: !this.state.autoplay });
  };
  _changeIcon = () => {
    let { leftIcon, rightIcon } = this.state;
    leftIcon = leftIcon ? undefined : icon_glass;
    rightIcon = rightIcon ? undefined : icon_music;
    this.setState({ leftIcon, rightIcon });
  };

  groupIntoThrees (children) {
    const output = []
    let currentGroup = []
  
    children.forEach((child, index) => {
      currentGroup.push(child)
  
      if (index % 3 === 2) {
        output.push(currentGroup)
        currentGroup = []
      }
    })
  
    return output
  }

  render() {
    const{clubs}=this.state;
    const l=clubs.length;
    let i=0;
    
   let clubss=this.groupIntoThrees(clubs);
    let settings ={
      infinite: false,
      speed:1000,
      arrows:true,
      slidesToShow:2,
      slidesToScroll:clubs.length,
      
    }
   let responsive= [
      {
        breakpoint: 960,
        settings: {
            slidesToShow: 3,
            slidesToScroll: 2
        }
    },
    {
        breakpoint: 480,
        settings: {
            slidesToShow: 1,
            slidesToScroll: 2
        }
    }
  ]
    return (
        <React.Fragment>    
          <div className="jumbotron j2"  style={{backgroundColor:'#f4f0c0 !important'}} >
                       <div className ="jumbotron j2"id="eventsection"
                       style={{backgroundImage: `url(/stars.png)`}} >
                            

     <h1 style={{marginTop:'5%', height:'10%', textAlign:'center' }}>DISCOVER ALL INSAT CLUBS</h1>
</div>
     
    <div ></div>
             
             <div className="row" style={{height: '99%'}} >         
               <div className="jumbotron j2 col-md-1" id="contour" 
             style={{float:"right" }}
             ></div>
         
          <div  className="  root jumbotron col-md-10" style={{backgroundColor :'#f4f0c0'}}  >
      <RBCarousel 
            
            className="carousel-fade roott  container-fluid     "
            
              animation={true}
              autoplay={this.state.autoplay}
              slideshowSpeed={5000}
              defaultActiveIndex={0}
              leftIcon={this.state.leftIcon}
              rightIcon={this.state.rightIcon}
              onSelect={this._onSelect}
              ref={r => (this.slider = r)}
              version={4}
            >
          
                    
               
               

               { 
               clubs.map((club,i)=>(

<div className="container-fluid" 
>
  
<img className="d-block w-100 images img-fluid"
  src={club.image}
/>


<div className="carousel-caption " id="clubLink">
  <button className="btn "
  style={{backgroundColor:'#f4f0c0 !important'}}
  >

    Go to {club.title}

  </button>
 
  </div>
  </div>
            

             
            
            
         ))}
 

              
        
 </RBCarousel>
 

              
       
              
 </div>
        </div>
        </div>
        <div className= 'container'
        >
          {
            clubs.length===0?(
              <div className='spinner-border' role='status'>
                <span className='sr-only'>Loading..</span>
              </div>
            ):(
              <Slider {...settings}>
                 {clubs.map(current=>(
                        <div className="out" key={current._id}>
                            <div className="card">
                                <img className="rounded-circle" alt={"users here"} src={current.image} height={56} width={56} />
                                <div className="card-body">
                                    <h5 className="card-title">{current.title}</h5>
                                    <small className="card-text text-sm-center text-muted">In your contacts</small>
                                    <br/>
                                    <button className="btn btn-sm follow btn-primary">Follow</button>
                                </div>
                            </div>
                        </div>
                    ))}
              </Slider>
            )
          }


        </div>
  </React.Fragment>
)

  
 

    
}
}
export default ClubSlider;
