import React, { Component } from 'react';
import img1 from '../assets/cstc.png';
import img2 from '../assets/back.png';
import img3 from "../assets/cap.png";
import ReactBootstrapCarousel from "react-bootstrap-carousel";
import "bootstrap/dist/css/bootstrap.css";
import "react-bootstrap-carousel/dist/react-bootstrap-carousel.css";
import "./styling.css";
import {Link} from 'react-router-dom';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import Slider from "react-slick";
import {HomeWrapper } from './style';
import {getCategories} from "../categoriesData";
import {getEvents} from "../eventData";
import AOS from 'aos';
import axios from 'axios';

import 'aos/dist/aos.css';
//color #f4f0c0

import {
    Container,
    Row,
    Col,
    Card,
    CardBody,
    CardFooter,
    Badge,
    Button
  } from "shards-react";
import CategoriesList from '../common/categoriesList'
import Login from './login';
import Navbar from './navbar';
import SearchPage from './searchpage';
import Slides from './slides';
import ClubParent from './clubParent';
class EventsParent
 extends Component {
    state = { 
        events:[],
        categories:[],
        filtered:'',
        selectedCategorie:''
    

     }
//ADDED STUFF
/*  constructor(props){
       super(props),
       this.state={events:[]};
     }
 */
 
   
      componentWillReceiveProps (){ 
        AOS.refresh(); 
      } 
     componentDidMount(){
        this.setState({events: getEvents(), categories: getCategories()});
        AOS.init({
            duration : 2000
          })

         /* axios.get('http://localhost:4000/eventsDB/')
          .then(response=>{
            this.setState({events: response.data});
          })
          .catch(function(error){console.log(error)});*/
    }
 //
    handleCategorieSelect=categorie=>{
        let {selectedCategorie,events,filtered}=this.state;
    
        this.setState({selectedCategorie: categorie});
        filtered= selectedCategorie
        ? events.filter(m=>m.categorie._id=== selectedCategorie._id)
        : events;
        
        
        
    }
   
    render() { 
          
 function renderRow(group, index){
  return (<div className="row" key={index}>
            {group.map(renderColumn)}
          </div>);
}

function renderColumn(image, index){
  return (<div className="col-xs-3" key={index}>
            <img src={image} />
         </div>);
}

function groupBy(amountOfItemsPerGroup, items){
  var groups = [], 
    group, 
    total = items.length;
  for (var i=0; i < total; i += amountOfItemsPerGroup) {
    group = items.slice(i, i+amountOfItemsPerGroup);
    groups.push(group);
  }
  return groups;
}
        const{selectedCategorie,events}=this.state;
        
        let filtered= selectedCategorie
        ? events.filter(m=>m.categorie._id=== selectedCategorie._id)
        : events;
        const l=filtered.length;
        var imageGroups = groupBy(4, filtered);
       // const nb=l/5;
        let i=0;
        var settings={
          dots:false,
          arrows: true,
          infinite:false,
          speed:300,
          slidesToShow:4,
          slidesToScroll:3
        }

        return ( 
        <React.Fragment>



       <div 
       data-aos="fade-up" className="jumbotron j2" id="the" style={{backgroundColor:'#faf8e6 !important'}} >
           
           <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1440 320">
  <path fill="white " fill-opacity="1" d="M0,64L1440,224L1440,320L0,320Z"></path>
</svg>
                       <div style={{marginTop:'0% !important'}} className ="jumbotron j2"id="eventsection"
                        >
                            

     <h1 id="withCarousel" style={{marginTop:'0%', height:'10%'}}>TOUS NOS CATEGORIES</h1>

     
    <div     >
          <CategoriesList
          style={{height:'20%', marginLeft:'20px'}}
            items={this.state.categories} 
            onItemSelect={this.handleCategorieSelect}
            selectedItem={this.state.selectedCategorie}
           />
           </div>

         <div className="j2">         <Container>
         <Link to='/'>see all</Link>
          {
            filtered.length===0?(
              <div className='spinner-border' role='status'>
                <span className='sr-only'>Loading..</span>
              </div>
            ):(
              
              <Slider {...settings}>
                 {filtered.map(function(current){
                        
          return(
            <HomeWrapper>
              
             
<div className="card-body " style={{margin:"10px"}}>
              <div className="card h-100 text-center shadow card bm--card-equal-height" style={{height:'100% !important'}}> 
                
                    <img src={current.image} alt="img1" className="card-img-top"/>

                
                <div className="card-body text-dark flex-fill">
                 <h4 className='card-title' > {current.title}</h4>
                    <p className="card-text text-secondary">
                    
                    </p>
          <a href="#" className="btn btn-outline-success" > Learn More </a>
                
                
                </div>

            </div>
            </div>
           
              
             
            </HomeWrapper>
          );
          
          
          })}
              </Slider>
            )
          }



</Container> </div>
</div>
</div>

<div id='eventsgallery'>                        <Slider {...settings}>
                 {filtered.map(function(current){
                        
          return(
            <div id ='eventimage'>
              <img src= {current.image} id="eventsitem"/>
              <div className="overlay">
                <div className='overlaycontent'>

                <h3> 
                 {current.title}
                </h3>
          <h4 >{current.start}</h4>
                <a href="#" className="btn btn-outline-success " > Learn More </a>
              </div>
              </div>
            </div>)})}</Slider>
                        </div>


            </React.Fragment>);
    }
}
 
export default EventsParent
;
