import React, { Component , useEffect} from 'react';
import img1 from '../assets/bright.gif';
import person from '../assets/person.gif';

import img2 from '../assets/insaat.png';
//import network from '../assets/grey.jpg';
import network from  '../assets/worldd.png';
import network2 from  '../assets/network2.png';
import Navbar from './navbar';
import Slides from './slides';
import img3 from "../assets/event.png";
import eventIcon from '../assets/eventicon.png';
import users from '../assets/users.png';
import sponsors from '../assets/sponsors.png';
import Aos from 'aos';
import 'aos/dist/aos.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCoffee, faSearch } from '@fortawesome/free-solid-svg-icons';
import CountUp from 'react-countup';

const parallax = document.getElementById('firstparallax');
window.addEventListener('scroll', function(){
    let offset = window.pageYOffset;
    document.getElementById('firstparallax').style.backgroundPositionY = offset*0. + "px";
    document.getElementById('layer1').style.backgroundPositionY = offset*0.7 + "px";
 //   document.getElementById('layer2').style.backgroundPositionY = offset*0.7 + "px";
})
const Welcome = () => {

    useEffect(()=>{
        Aos.init({duration:4000});
    },[]);


    return (
        <React.Fragment>
      
        <Navbar className="ab"  style={{position: 'absolute'}}/>
 
    
          
          
<Slides className="root sliderr" style={{ position: 'relative',minHeight: '100vh', height: '100vh' }} />
    
   
      
      
    

      <section id='about'  style={{ padding:'0', margin:0
           }} > 
           

           
           <div className="row "   id="layer1" 
           style={{
            backgroundImage:`url(${network})`, padding:'0', margin:'0'
           }} >
           <div
            data-aos="fade-right"
            data-aos-offset="200"
            data-aos-delay="0"
            data-aos-duration="800"
            
            data-aos-mirror="true"
            data-aos-once="false"
            data-aos-anchor-placement="top-center"
              className="col-md-3">
                   <img  className='welcomeIM' style={{ height:'80%',width:'100%', float:'left'}}
                    src= {img1}/>
               </div>
          
          
          
          
           <div 
              
              className="col-md-6" style={{paddingLeft:'4%'}} >
                <h1 style={{textAlign:"center", paddingTop:"20%", paddingRight:'4%'}}>
                Bright INSAT, at a glance ..
                </h1>
                <h2  style={{textAlign:"center"}}>
                    You missed some interesting events because there's just too much to remember? <br/>
                   BRIGHT INSAT  is here for you ! <br/> Being aware of all the upcoming events has never been easier ! 
 
                </h2>
 
            </div>
          
          <div className='col-md-3'></div>

              
              
              
              

              
           </div>
           
           <div className="row cell" style={{padding:'4rem 2rem', backgroundColor:'#545454', minHeight:'550px'}} >
               <div className='col-md-3 cells'>
               <span className="input-group-addon">
                   <img src={users}></img>
                   </span>

                   <h1>
                   
               <CountUp end={4500} />
               </h1>
               
               <h6 style={{textAlign:'center !important'}}>users</h6>
               </div>
               <div className='col-md-3 cells'>
               <span className="input-group-addon">
                   <img src={eventIcon}></img>
                   </span>

                   <h1>
                   
               <CountUp end={8000} />
               </h1>
               
               <h6 style={{textAlign:'center !important'}}>our events</h6>
               </div>
               <div className='col-md-3 cells'>
               <span className="input-group-addon">
                   <img src={sponsors}></img>
                   </span>

                   <h1>
                   
               <CountUp end={2000} />
               </h1>
               
               <h4 style={{textAlign:'center !important'}}>Partnership</h4>
               </div>
          

           </div>
           <div style={{padding:'0', margin:'0'}} >
           
           <div className="parallax" id='firstparallax' style={{backgroundImage:`url(${img2})`,backgroundAttachment:'fixed',
            backgroundSize:'cover',textAlign:'center', maxHeight:'400px', position:'relative'}}><p >div1</p>
            <div className="parallaxtext row cell"> 
             <div className='col-md-3 cells'>
               <span className="input-group-addon">
                   <img src={users}></img>
                   </span>

                   <h1>
                   
               <CountUp end={4500} />
               </h1>
               
               <h6 style={{textAlign:'center !important'}}>users</h6>
               </div>
               <div className='col-md-3 cells'>
               <span className="input-group-addon">
                   <img src={eventIcon}></img>
                   </span>

                   <h1>
                   
               <CountUp end={8000} />
               </h1>
               
               <h6 style={{textAlign:'center !important'}}>our events</h6>
               </div>
               <div className='col-md-3 cells'>
               <span className="input-group-addon">
                   <img src={sponsors}></img>
                   </span>

                   <h1>
                   
               <CountUp end={2000} />
               </h1>
               
               <h4 style={{textAlign:'center !important'}}>Partnership</h4>
               
          

           </div>
            
            </div></div>
           
           
   
           </div>
           </section> 
      </React.Fragment>
      );
}
 
export default Welcome;
