import React from 'react';
import CommentList from "./commentList";
import CommentForm from "./commentForm.jsx";
import PastEvents from './pastevents';
import Sidebar from './sidebar';
import EditForm from './editForm';

export default class Event extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      comments: ['helloo', "hiii", 'good'],
      loading: false
    };

    //this.addComment = this.addComment.bind(this);
  }


   addComment(comment) {
    this.setState({
      loading: false,
      comments: [comment, ...this.state.comments]
    });
  }

  render() {

  console.log(this.state.comments);

  return (


    <div className="App">


<Sidebar/>




<EditForm/>

<div class="fb-comments" data-href="http://localhost:3000/" data-numposts="5" data-width=""></div>
 <div className='j2'>         
      <PastEvents/></div>
      <div className='row'>
      <div className='col-2'></div>
      <div className="col-8  pt-3 border-right">
      <div class="fb-like" data-href="https://developers.facebook.com/docs/plugins/" data-width="" data-layout="standard" data-action="like" data-size="small" data-share="true"></div>
            <h6>FEEDBACK</h6>
            <CommentForm addComment={this.addComment} />
          </div>
          <div className='col-2'></div>
          </div>   
      <div className='row'>
      <div className='col-2'></div>
      <div className="col-8  pt-3 bg-white">
      <div class="fb-comments" data-href="http://localhost:3000/App.js" data-numposts="5" data-width="200px"></div>
            <CommentList
              loading={this.state.loading}
              comments={this.state.comments}
            />
          </div>
          <div className='col-2'></div>
      </div>

</div>


  );
}

}
