import React, { Component } from 'react';
import sponsor1 from "../images/sponsor1.jpg"
import sponsor2 from "../images/sponsor2.jpg"
import sponsor3 from "../images/sponsor3.jpg"
import sponsor4 from "../images/sponsor4.jpg"
import sponsor5 from "../images/sponsor5.png"
import sponsor6 from "../images/sponsor6.png"
import "./sponsors.css";

import axios from 'axios';
import $ from 'jquery';

const MongoClient = require('mongodb').MongoClient;
const assert = require('assert');
class Sponsor extends Component {


  constructor(){
    super()
    this.scroll = this.scroll.bind(this)
  }
  state = {
    data: [],
    id: 0,
    message: null,
    intervalIsSet: false,
    idToDelete: null,
    idToUpdate: null,
    objectToUpdate: null,
  };

  // when component mounts, first thing it does is fetch all existing data in our db
  // then we incorporate a polling logic so that we can easily see if our db has
  // changed and implement those changes into our UI
  componentDidMount() {
    this.getDataFromDb();
    if (!this.state.intervalIsSet) {
      let interval = setInterval(this.getDataFromDb, 1000);
      this.setState({ intervalIsSet: interval });
    }
  }

  // never let a process live forever
  // always kill a process everytime we are done using it
  componentWillUnmount() {
    if (this.state.intervalIsSet) {
      clearInterval(this.state.intervalIsSet);
      this.setState({ intervalIsSet: null });
    }
  }

  // just a note, here, in the front end, we use the id key of our data object
  // in order to identify which we want to Update or delete.
  // for our back end, we use the object id assigned by MongoDB to modify
  // data base entries

  // our first get method that uses our backend api to
  // fetch data from our data base
  getDataFromDb = () => {
    fetch('http://localhost:3001/api/getData')
      .then((data) => data.json())
      .then((res) => this.setState({ data: res.data }));
  };

  // our put method that uses our backend api
  // to create new query into our data base
  putDataToDB = (message) => {
    let currentIds = this.state.data.map((data) => data.id);
    let idToBeAdded = 0;
    while (currentIds.includes(idToBeAdded)) {
      ++idToBeAdded;
    }

    axios.post('http://localhost:3001/api/putData', {
      id: idToBeAdded,
      message: message,
    });
  };

  // our delete method that uses our backend api
  // to remove existing database information
  deleteFromDB = (idTodelete) => {
    parseInt(idTodelete);
    let objIdToDelete = null;
    this.state.data.forEach((dat) => {
      if (dat.id == idTodelete) {
        objIdToDelete = dat._id;
      }
    });

    axios.delete('http://localhost:3001/api/deleteData', {
      data: {
        id: objIdToDelete,
      },
    });
  };

  // our update method that uses our backend api
  // to overwrite existing data base information
  updateDB = (idToUpdate, updateToApply) => {
    let objIdToUpdate = null;
    parseInt(idToUpdate);
    this.state.data.forEach((dat) => {
      if (dat.id == idToUpdate) {
        objIdToUpdate = dat._id;
      }
    });

    axios.post('http://localhost:3001/api/updateData', {
      id: objIdToUpdate,
      update: { message: updateToApply },
    });
  };
  scroll(direction){
    let far = $( '.image-container' ).width()/2*direction;
    let pos = $('.image-container').scrollLeft() + far;
    $('.image-container').animate( { scrollLeft: pos }, 1000)
  }



  render(){
    const { data } = this.state;
  return (
    <div >
       {data.length <= 0
            ? 'NO DB ENTRIES YET'
            : data.map((dat) => (
      <div className="main">
        <h3>Meet our partners!</h3>
      <div className="wrapper">
        <a className="prev" onClick={this.scroll.bind(null,-1)}>&#10094;</a>
        <div className="image-container">
          <div className="image"><img src = {sponsor1}></img></div>
          <div className="image"><img src = {sponsor2}></img></div>
          <div className="image"><img src = {sponsor3}></img></div>
          <div className="image"><img src = {sponsor4}></img></div>
          <div className="image"><img src = {sponsor5}></img></div>
          <div className="image"><img src = {sponsor6}></img></div> 
          <div className="image"><img src = {sponsor1}></img></div>
          <div className="image"><img src = {sponsor2}></img></div>
          <div className="image"><img src = {sponsor3}></img></div>
          <div className="image"><img src = {dat.image}></img></div>
          
        </div>
        <a className="next" onClick={this.scroll.bind(null,1)}>&#10095;</a>
      </div>
    </div>
             )) }
    </div>
      
  )}
    
  
}

export default Sponsor;
