import React, { Component } from 'react';
import {Form, Modal, Button, Row, Col, } from 'react-bootstrap';
import img from '../assets/defaultpic.png';
import { faBullseye } from '@fortawesome/free-solid-svg-icons';
import ImageUpload from './imageUpload';
import ImageUploader from 'react-images-upload';
import axios from'axios';


class RenderBody extends Component {
    constructor(props){
        super(props);
        this.state={snackbaropen: false, snackbarmsg:'', title:'', category:'', start:'', end:'', 
    image:'', dsc:'',  file: '', imagePreviewUrl: ''}
        this.handleSubmit= this.handleSubmit.bind(this);
        this.state = { pictures: [] };
        this.onDrop = this.onDrop.bind(this);
        this._handleImageChange = this._handleImageChange.bind(this);
     

    }


    snackbarClose=(event)=>{
        this.setState({snackbaropen:false});
    }


    handleSubmit(event){
       
        this.setState({ 'selectedFiles': event.target.files });
    console.log(this.state.selectedFiles);
        event.preventDefault();
   let data = new FormData();
       //Append files to form data
       let files = this.state.files;
       for (let i = 0; i < files.length; i++) {
         data.append("file", files[i], files[i].name);
       }
     console.log('data');
   // data.append ('file', this.state.)
   // alert(event.target.EventName.value);
   //event.preventDefault();
   /*const data = { title:this.state.title,category:this.state.category,dsc:this.state.dsc
    ,start:this.state.start,end:this.state.end, image:this.state.image}
    */
   /*******************************const formData = new FormData();
   formData.append('title','');
   formData.append('category','');
   formData.append('dsc','');
   formData.append('start','');
   formData.append('end','');
   formData.append('image','');
   
   
   axios.post("http://localhost:5000/events/add/", formData)
   
    .then((result)=>
    {
        alert(result);
    },(error)=>{
        alert('Failed')
    });
************************************************/
  

    let options = {
        headers: {
          'Content-Type': 'multipart/form-data'
        },
        method: 'POST'
      };
    
      options.body = new FormData();
      for (let key in data) {
        options.body.append(key, data[key]);
      }
    
     fetch('http://localhost:5000/events/add/', options)
         
    .then(res=>res.json())
    .then((result)=>
    {
        alert(result);
    },(error)=>{
        alert('Failed')
    }) ;

    window.location.reload();
}
   
onChangeHandler=event=>{
    this.setState({ [event.target.name]:event.target.value });
    console.log (this.state.title)
}
onDrop(picture) {
    this.setState({
        pictures: this.state.pictures.concat(picture),
    });
}



_handleImageChange(e) {
    e.preventDefault();

    let reader = new FileReader();
    let file = e.target.files[0];

    reader.onloadend = () => {
      this.setState({
        file: file,
        imagePreviewUrl: reader.result
      });
    }

    reader.readAsDataURL(file)
  }

   
    render() { 
        let {imagePreviewUrl} = this.state;
        let $imagePreview = null;
        if (imagePreviewUrl) {
          $imagePreview = (<img src={imagePreviewUrl} />);
        }
    
        return ( 
            <div className="container">
            
            
            
            <Modal
            {...this.props}
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            centered
          >
            <Modal.Header closeButton>
              <Modal.Title id="contained-modal-title-vcenter">
                Add a New Event
              </Modal.Title>
            </Modal.Header>
            <Modal.Body>
             
                 <Row>
                     <Col sm={6}>
                         <Form onSubmit={this.handleSubmit}
                         onChange={this.onChangeHandler}
                         >
                             <Form.Group controlId="title">
                                 <Form.Label>Event name</Form.Label>
                                 <Form.Control
                                 type='text'
                                 name='title'
                                 required
                                
                                 
                                 />
                             </Form.Group>
                             <Form.Group controlId="start">
                                 <Form.Label>Start date</Form.Label>
                                 <Form.Control
                                 type='text'
                                 name='start'
                                 required
                                 placeholder=''
                                 
                                 />
                             </Form.Group>
                             <Form.Group controlId="end">
                                 <Form.Label>End date</Form.Label>
                                 <Form.Control
                                 type='text'
                                 name='end'
                                 required
                                 placeholder=''
                                 
                                 />
                             </Form.Group>
                             <Form.Group controlId="dsc">
                                 <Form.Label>Description</Form.Label>
                                 <Form.Control
                                 type='text'
                                 name='dsc'
                                 required
                                 placeholder=''
                                 
                                 />
                             </Form.Group>
                             <Form.Group controlId="category">
                                 <Form.Label>Category</Form.Label>
                                 <Form.Control
                                 type='text'
                                 name='category'
                                 required
                                 placeholder=''
                                 
                                 />
                             </Form.Group>
                             <Form.Group controlId="image">
                             <img
                               src={imagePreviewUrl || img}
                               alt="Uploaded Image"
                               height='200'
                                width='200'
                                />
                         <Form.File id="exampleFormControlFile1" 
                         label="Event Image"
                         onChange={this._handleImageChange} />
                         </Form.Group>
                         <Form.Group>
                             <Button vaiant='primary'
                             type='submit'>
                                 Add 
                             </Button>
                         </Form.Group>
                        
                         </Form>
                     </Col>
                     <Col>

                   
                   
  
                     
                     </Col>
                 </Row>
            </Modal.Body>
            <Modal.Footer>
              <Button onClick={this.props.onHide}>Close</Button>
            </Modal.Footer>
          </Modal>
          </div>
         );
    }
}
 
export default RenderBody;
