import img1 from '../assets/ins.jpg';
import img2 from '../assets/insat.png';
import Slider from 'react-slick';
import {getClubs} from "../datafolder/clubData";
import './styling.css';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import React,{Component, useState,useEffect} from "react";
import "./styling.css";
import RBCarousel from "react-bootstrap-carousel";
import "react-bootstrap-carousel/dist/react-bootstrap-carousel.css";

const styles = { height: 400, width: "100%" };
const icon_glass = <span className="fa fa-glass" />;
const icon_music = <span className="fa fa-music" />;

class ClubSlider extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        clubs:getClubs(),
      autoplay: true,
      slideIndex:0
    
    };
  }

 
  slider=()=>{
    var slideshowImages = document.querySelectorAll(".intro.slideshow-img:first-child")
    var delay = 3000;
    let counter = 0;

    slideshowImages[counter].style.display ="block";
    setInterval(function(){
      slideshowImages[counter].style.display='none';
      counter = (counter)+1 % slideshowImages.length;
      slideshowImages[counter].style.display ="block";
  
    }, delay);
  
  }

  onSelect = (active, direction) => {
    console.log(`active=${active} && direction=${direction}`);
  };
  visiableOnSelect = active => {
    console.log(`visiable onSelect active=${active}`);
  };
  slideNext = () => {
    this.slider.slideNext();
  };
  slidePrev = () => {
    this.slider.slidePrev();
  };
  goToSlide = () => {
    this.slider.goToSlide(1);
  };
  autoplay = () => {
    this.setState({ autoplay: !this.state.autoplay });
  };
  _changeIcon = () => {
    let { leftIcon, rightIcon } = this.state;
    leftIcon = leftIcon ? undefined : icon_glass;
    rightIcon = rightIcon ? undefined : icon_music;
    this.setState({ leftIcon, rightIcon });
  };

  groupIntoThrees (children) {
    const output = []
    let currentGroup = []
  
    children.forEach((child, index) => {
      currentGroup.push(child)
  
      if (index % 3 === 2) {
        output.push(currentGroup)
        currentGroup = []
      }
    })
  
    return output
  }

 
  render() {
    const{clubs, slideIndex}=this.state;
    const l=clubs.length;

  var settings={
    autoplay: true,
    className: "slider variable-width",
    speed: 2000,
    autoplaySpeed: 2000,
    infinite: true,
    cssEase: "linear",
    dots:false,
    arrows: true,
    speed:300,
    slidesToShow:7,
    slidesToScroll:3,
    responsive: [
    
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 2,
          initialSlide: 2
        }
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1
        }
      }
    ]
  }

    return (

        <React.Fragment>      <div 
        id="clubsection" style={{ backgroundColor:'white'}}>
          <div className="row" id='one' style={{marginBottom:'5%', marginTop:'10%', textAlign:"center"}}>
            <h3> In Our Dream Land.. <br/> We have more than 20 active club. </h3>
          </div>
   <div id ='clubgallery' style={{objectFit:'cover !important'}}>
          <Slider {...settings}>
                 {clubs.map(function(current){
                        
          return(

              
            <div  id ='clubimage' style={{objectFit:'cover !important', minHeight:'100%'}} >
              <img src= {current.image} id="clubsitem" style={{objectFit:'cover !important'}}/>
              <div className="overlay">
                <div className='overlaycontent' >
                <div id='inneroverlay'>                <p
              style={{borderStyle:'ridge'}}> {current.dsc} </p>
                </div>

              </div>
              </div>
            </div>

            )})}
       </Slider>
              </div>
              </div>
       
              
 
 
         
              <div className='row'style={{ backgroundColor:'white'}} >
           
           <div className="parallax" id='parallax_join' style={{backgroundImage:`url(${img2})`,backgroundAttachment:'fixed',
            backgroundSize:'cover',textAlign:'center', maxHeight:'80vh', position:'relative'}}>
               </div>
               </div>

  </React.Fragment>
)

  
 

    
}
}
export default ClubSlider;
