import React, { Component } from 'react';
import {Form, Modal, Button, Row, Col, } from 'react-bootstrap'/* 
import  SnackBar  from '@material-ui/core/SnackBar';
import IconButton from '@material-ui/core/IconButton' */
import { faBullseye } from '@fortawesome/free-solid-svg-icons';
import ImageUpload from './imageUpload';
import ImageUploader from 'react-images-upload';
class RenderBody extends Component {
    constructor(props){
        super(props);
        this.state={snackbaropen: false, snackbarmsg:'', selectedFiles:null}
        this.handleSubmit= this.handleSubmit.bind(this);
        this.state = { pictures: [] };
        this.onDrop = this.onDrop.bind(this);

    }


    snackbarClose=(event)=>{
        this.setState({snackbaropen:false});
    }


    handleSubmit(event){
        let files = event.target.files;
        this.setState({ selectedFiles: files })
    event.preventDefault();
   let data = new FormData();
       //Append files to form data
       
       for (let i = 0; i < this.state.selectedFiles.length; i++) {
         data.append("file", this.state.selectedFiles[i], this.state.selectedFiles[i].name);
       }
 
   // data.append ('file', this.state.)
   // alert(event.target.EventName.value);
    fetch('http://localhost:4000/eventsDB/add',{
    method:'POST'  ,
    headers:{
          
          'Accept': 'application/json',
          'Content-Type': 'application/json'
      }  ,
      body:data
    })
    .then(res=>res.json())
    .then((result)=>
    {
        alert(result);
    },(error)=>{
        alert('Failed')
    }) 
}

onDrop(picture) {
    this.setState({
        pictures: this.state.pictures.concat(picture),
    });
}

    state = {  }
    render() { 
        return ( 
            <div className="container">
            
            
            
            <Modal
            {...this.props}
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            centered
          >
            <Modal.Header closeButton>
              <Modal.Title id="contained-modal-title-vcenter">
                Add a New Event
              </Modal.Title>
            </Modal.Header>
            <Modal.Body>
             
                 <Row>
                     <Col sm={6}>
                         <Form onSubmit={this.handleSubmit}
                         onChange={this.onChangeHandler}
                         >
                             <Form.Group controlId="EventName">
                                 <Form.Label>Event name</Form.Label>
                                 <Form.Control
                                 type='text'
                                 name='EventName'
                                 required
                                 placeholder='Event name'
                                 
                                 />
                             </Form.Group>
                             <Form.Group controlId="EventStart">
                                 <Form.Label>Start date</Form.Label>
                                 <Form.Control
                                 type='text'
                                 name='EventStart'
                                 required
                                 placeholder=''
                                 
                                 />
                             </Form.Group>
                             <Form.Group controlId="EventEnd">
                                 <Form.Label>End date</Form.Label>
                                 <Form.Control
                                 type='text'
                                 name='EventEnd'
                                 required
                                 placeholder=''
                                 
                                 />
                             </Form.Group>
                             <Form.Group controlId="EventDSC">
                                 <Form.Label>Description</Form.Label>
                                 <Form.Control
                                 type='text'
                                 name='EventDSC'
                                 required
                                 placeholder=''
                                 
                                 />
                             </Form.Group>
                             <Form.Group controlId="EventCategory">
                                 <Form.Label>Category</Form.Label>
                                 <Form.Control
                                 type='text'
                                 name='EventCategory'
                                 required
                                 placeholder=''
                                 
                                 />
                             </Form.Group>
                         <Form.Group>
                             <Button vaiant='primary'
                             type='submit'>
                                 Add 
                             </Button>
                         </Form.Group>
                         
                         
                         </Form>
                     </Col>
                     <Col>
                     <ImageUpload
            />
                     
                     </Col>
                 </Row>
            </Modal.Body>
            <Modal.Footer>
              <Button onClick={this.props.onHide}>Close</Button>
            </Modal.Footer>
          </Modal>
          </div>
         );
    }
}
 
export default RenderBody;
