import React, { Component } from 'react';
//import { Link } from 'react-router-dom';
import { HashRouter as Router, Route, Link, NavLink } from 'react-router-dom';
import'../styleSign.css'
import TermsOfService from './termsOfService';
import img3 from '../assets/insss.png';


class SignUpForm extends Component {
    constructor() {
        super();

        this.state = {
            email: '',
            password: '',
            name: '',
            hasAgreed: false,
			phoneNumber: '',
			club: ''
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e) {
        let target = e.target;
        let value = target.type === 'checkbox' ? target.checked : target.value;
        let name = target.name;

        this.setState({
          [name]: value
        });
    }

    handleSubmit(e) {
        e.preventDefault();

        console.log('The form was submitted with the following data:');
        console.log(this.state);
    }

    render() {
        return (
        <div className="FormCenter" style={{backgroundImage:`url(${img3})`}}>
            <form onSubmit={this.handleSubmit} className="FormFields">

			  <div className="FormField">
                <label className="FormField__Label" htmlFor="club">Club</label>
                <input type="text" id="club" className="FormField__Input" placeholder="Enter name of your club" name="club" value={this.state.club} onChange={this.handleChange} />
              </div>

              <div className="FormField">
                <label className="FormField__Label" htmlFor="name">Full Name</label>
                <input type="text" id="name" className="FormField__Input" placeholder="Enter your full name" name="name" value={this.state.name} onChange={this.handleChange} />
              </div>

			  <div className="FormField">
                <label className="FormField__Label" htmlFor="phoneNumber">phone number</label>
                <input type="number" id="phoneNumber" className="FormField__Input" placeholder="Enter your phone number" name="phoneNumber" value={this.state.phoneNumber} onChange={this.handleChange} />
              </div>

			  <div className="FormField">
                <label className="FormField__Label" htmlFor="password">Password</label>
                <input type="password" id="password" className="FormField__Input" placeholder="Enter your password" name="password" value={this.state.password} onChange={this.handleChange} />
              </div>

			  <div className="FormField">
                <label className="FormField__Label" htmlFor="email">E-Mail Address</label>
                <input type="email" id="email" className="FormField__Input" placeholder="Enter your email" name="email" value={this.state.email} onChange={this.handleChange} />
              </div>


              <div className="FormField">
                <label className="FormField__CheckboxLabel">
						<input className="FormField__Checkbox" type="checkbox" name="hasAgreed" value={this.state.hasAgreed} onChange={this.handleChange} /> I agree all statements in  
            <Link to= "/termsOfService" className="FormField__TermsLink">terms of service</Link>
						<Route path="/termsOfService" component={TermsOfService} />
                </label>
              </div>

			  <div className="FormField">
                  <button className="FormField__Button mr-20">Sign Up</button> <Link to="/sign-in" className="FormField__Link">I'm already member</Link>
              </div>
            </form>
          </div>
        );
    }
}

export default SignUpForm;
