import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import "./Authentification.css";
import { connect } from "react-redux";
import EmailIcon from "@material-ui/icons/Email";
import LockOpenIcon from "@material-ui/icons/LockOpen";
import LockIcon from "@material-ui/icons/Lock";
import {
  Button,
  Input,
  InputAdornment,
  Grid,
  CircularProgress,
} from "@material-ui/core";
import { register } from "./actions/authActions";
import PropTypes from "prop-types";

import AccountCircle from "@material-ui/icons/AccountCircle";


import ReCAPTCHA from "react-google-recaptcha";
class Register extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: "",
      email: "",
      password: "",
      phone_number: "",
      club_title : "",
      password_confirmation: "",
      registrationErrors: [],
      componentState: "",
      notrobot:""
    };
    this.onSubmit = this.onSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.testerApply = this.testerApply.bind(this);
    this.developerRegister = this.developerRegister.bind(this);
    this.RecaptchaHandle=this.RecaptchaHandle.bind(this);
  }
  static propTypes = {
    isAuthenticated: PropTypes.bool,
    register: PropTypes.func.isRequired,
  };

  componentDidMount() {
    this.setState(
      {
        registrationErrors: null,
        componentState: "developer",
      },
      () => {
        let spinner = document.getElementById("spinner");
        if (spinner) spinner.style.visibility = "hidden";
      }
    );
  }
  componentWillUpdate(nextProps, prevProps) {
    if (nextProps.isAuthenticated) {
      this.props.history.push("/developer");
    } else if (this.props.error !== prevProps.error) {
      console.log(this.props.error);
    }
  }
  getErrors() {
    this.setState({
      registrationErrors: this.props.error,
    });
  }
  
  onSubmit(event) {
    if(!this.state.notrobot){
      this.setState({notrobot:false})
    }
    else{
    event.preventDefault();
    let spinner = document.getElementById("spinner");
    spinner.style.visibility = "visible";
    const { username, email, password, password_confirmation } = this.state;

    // Create user object
    const newUser = {
      username,
      email,
      password,
      password_confirmation,
    };

    // Attempt to register
    this.props.register(newUser);
  }
  }
/*
  onSubmit = (event) => {
    event.preventDefault();
    fetch('/api/register', {
      method: 'POST',
      body: JSON.stringify(this.state),
      headers: {
        'Content-Type': 'application/json'
      }
    })
    .then(res => {
      if (res.status === 200) {
        this.props.history.push('/');
      } else {
        const error = new Error(res.error);
        throw error;
      }
    })
    .catch(err => {
      console.error(err);
      alert('Error register!');
    });
  }

*/


  handleChange(event) {
    this.setState({
      [event.target.name]: event.target.value,
    });
  }

  componentDidUpdate(prevProps) {
    let spinner = document.getElementById("spinner");
    if (spinner) spinner.style.visibility = "hidden";
    if (this.props.error.msg !== prevProps.error.msg) {
      if (this.props.error.id === "REGISTER_FAIL") {
        this.setState({ registrationErrors: this.props.error.msg }, () => {});
      } else {
        this.setState({ registrationErrors: null });
      }
    }
  }
  testerApply() {
    this.setState(
      {
        componentState: "tester",
      },
      () => {
        let area = document.getElementById("area");
        let leftSide = document.getElementById("left-side");
        area.style.backgroundColor = "#61B769";
        leftSide.style.backgroundColor = "#74C07B";
      }
    );
  }
  developerRegister() {
    this.setState(
      {
        componentState: "developer",
      },
      () => {
        let area = document.getElementById("area");
        let leftSide = document.getElementById("left-side");
        area.style.backgroundColor = "#0D4D7B";
        leftSide.style.backgroundColor = "#2A638B";
      }
    );
  }

  RecaptchaHandle(value){
    if(value)
      this.setState({notrobot:true})
  }
  render() {
    return (
      <div id="area" className="area">
        <ul className="circles">
          <li></li>
          <li></li>
          <li></li>
          <li></li>
          <li></li>
          <li></li>
          <li></li>
          <li></li>
          <li></li>
          <li></li>
        </ul>

        <div className="outer">
  
          <div className="middle">
            <div className="inner-login">
              <div className="left-side" id="left-side">
              
                
              </div>
              {this.state.componentState === "developer" ? (
                <div id="right-side-developer" className="right-side">
                  <form onSubmit={this.onSubmit}>
                    <div className="centered">
                      
                      <div
                        id="register-section"
