import React, { Component } from 'react';
import './styling.css';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import interactionPlugin from "@fullcalendar/interaction";
import bootstrapPlugin from '@fullcalendar/bootstrap';
import PopUp from './popup';
import { faCoffee, faSearch } from '@fortawesome/free-solid-svg-icons';
import Dialog from 'react-bootstrap-dialog'
import {Button,Modal} from 'react-bootstrap';
import MyModal from './modal';
import ModalBody from "react-bootstrap/ModalBody";
import ModalHeader from "react-bootstrap/ModalHeader";
import ModalFooter from "react-bootstrap/ModalFooter";
import ModalTitle from "react-bootstrap/ModalTitle";
import {getEvents} from "../eventData";
import {getCategories} from "../categoriesData";
export default class TheCalendar extends React.Component {
  constructor(props) {
    super(props);

    this.replaceModalItem = this.replaceModalItem.bind(this);
    this.saveModalDetails = this.saveModalDetails.bind(this);
    this.handleEventClick = this.handleEventClick.bind(this)
    this.state = {
      modal: false,
      requiredItem: 0,
      events:[],
      categories:[],
      event:{title: 'my event',start: '2020-04-01', end:'2020-04-02'},
      seen: false
    }
  }
  replaceModalItem(info) {
    this.setState({
      requiredItem: this.state.events.indexOf(info.event)
    });
  }

  saveModalDetails(item) {
    const requiredItem = this.state.requiredItem;
    let tempevents = this.state.events;
    tempevents[requiredItem] = item;
    this.setState({ events: tempevents });
  }

  deleteItem(index) {
    let tempevents = this.state.events;
    tempevents.splice(index, 1);
    this.setState({ events: tempevents });
  }
    
  componentDidMount(){
    this.setState({events: getEvents(), categories: getCategories()});
  }

    handleDateClick = (arg) => { // bind with an arrow function
       
      }
      formatEvents() {
        return this.state.events.map(event => {
                  const {title, end, start} = event
      
                  let startTime = new Date(start)
                  let endTime = new Date(end)
      
                  return {
                    title, 
                    start: startTime,
                    end: endTime, 
                    extendedProps: {...event}
                  }
              })
      }
    /*  handleDragStop=(eventClickInfo)=>{
        
        this.dialog.show({
            title: title,
            body: '<h1>HELLO</h1>',
            actions: [
              Dialog.CancelAction(),
              Dialog.OKAction()
            ],
            bsSize: 'small',
            onHide: (dialog) => {
              dialog.hide()
              console.log('closed by clicking background.')
            }
          })

      }*/
      handleDragStop=()=>{
        return(<React.Fragment>
           <div class="modal fade" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">Required PopUp </div>
      
        </React.Fragment>)
            
      }
      toggle = () => {
        this.setState({ modal: !this.state.modal });
      };
    
      handleEventClick = ({ event, el }) => {
        this.toggle();
        this.setState({ event });
      };
      // handleEventClick= ({event}) => {
        // openevent is a function I wrote to open a form to edit that appointment
       // this.props.openAppointment(event.extendedProps)
 //this.dialog.showAlert('Hello Dialog!')}
 
  /*  handleEventDrop = (info) => {
            if(window.confirm("Are you sure you want to change the event date?")){
                console.log('change confirmed')
    
                // updateAppointment is another custom method
                this.props.updateAppointment({...info.event.extendedProps, start: info.event.start, end: info.event.end})
    
            } else {
                console.log('change aborted')
            }
       }*/
  render() {
    const requiredItem = this.state.requiredItem;
    let modalData = this.state.events[requiredItem];
    let handle=this.handleDragStop;
    return (
    <div className='jumbotron'>
              <FullCalendar dateClick={this.handleDateClick} 
              plugins={[ dayGridPlugin, interactionPlugin,bootstrapPlugin ]}
               
                editable={true}
                selectable={true}
                selectMirror={true}
               // eventClick = {function(calEvent, jsEvent, view, resourceObj) {alert(calEvent.title)}}
                eventDragStop={this.handleDragStop}
                eventClick={this.handleEventClick}
                select={this.handleEventClick}
                dateClick={this.handleEventClick}
                events={this.formatEvents()}
                slotWidth={60}
                eventColor= '#378006'
                eventBackgroundColor='white'
                eventBorderColor="black"
                eventTextColor='red'
                customButtons={{
                  myCustomButton: {
                      text: 'Add new event ',
                      click: handle
                      },
                  }
             }
              header={{
                  right:  'prev,next today ',
                  center: 'title',
                  left:'myCustomButton,timeGridWeek,timeGridDay,listWeek'
              }}
             
                
              />
               <Modal
          isOpen={this.state.modal}
          toggle={this.toggle}
          className={this.props.className}
        >
          <ModalHeader toggle={this.toggle}>
            {this.state.event.title}
          </ModalHeader>
          <ModalBody>
            <div>
              <p>{this.state.event.start}</p>
            </div>
          </ModalBody>
          <ModalFooter>
            <Button color="primary">Do Something</Button>{" "}
            <Button color="secondary" onClick={this.toggle}>
              Cancel
            </Button>
          </ModalFooter>
        </Modal>
              <Dialog className="modal-dialog modal-dialog-centered"
              ref={(component) => { this.dialog = component }}
              body={"<h1>HELLO</h1>"} />
              
              </div>


)
  }
}
