import img1 from '../assets/ins.jpg';
import img2 from '../assets/insat.png';
import {getClubs} from "../datafolder/clubData";
import './styling.css';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import React,{Component, useState,useEffect} from "react";
import "./styling.css";
import RBCarousel from "react-bootstrap-carousel";
import "react-bootstrap-carousel/dist/react-bootstrap-carousel.css";

const styles = { height: 400, width: "100%" };
const icon_glass = <span className="fa fa-glass" />;
const icon_music = <span className="fa fa-music" />;

class ClubSlider extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        clubs:getClubs(),
      autoplay: true,
      slideIndex:0
    
    };
  }
 
  pludDivs= (n) =>{
    const { images } = this.state;
    if (n > images.length) {
      this.setState({ slideIndex: 1 });
    }
    if (n < 1) {
      this.setState({ slideIndex: images.length - 1 });      
    }
  }
  onSelect = (active, direction) => {
    console.log(`active=${active} && direction=${direction}`);
  };
  visiableOnSelect = active => {
    console.log(`visiable onSelect active=${active}`);
  };
  slideNext = () => {
    this.slider.slideNext();
  };
  slidePrev = () => {
    this.slider.slidePrev();
  };
  goToSlide = () => {
    this.slider.goToSlide(1);
  };
  autoplay = () => {
    this.setState({ autoplay: !this.state.autoplay });
  };
  _changeIcon = () => {
    let { leftIcon, rightIcon } = this.state;
    leftIcon = leftIcon ? undefined : icon_glass;
    rightIcon = rightIcon ? undefined : icon_music;
    this.setState({ leftIcon, rightIcon });
  };

  groupIntoThrees (children) {
    const output = []
    let currentGroup = []
  
    children.forEach((child, index) => {
      currentGroup.push(child)
  
      if (index % 3 === 2) {
        output.push(currentGroup)
        currentGroup = []
      }
    })
  
    return output
  }

 
  render() {
    const{clubs, slideIndex}=this.state;
    const l=clubs.length;
    //let i=0;
    window.addEventListener('scroll', function(){
      let offset = window.pageYOffset;
      document.getElementById('clubparallax').style.backgroundPositionY = offset*0.7 + "px";
  })
    return (
 
        <React.Fragment>    
          <div className="jumbotron j2 parallax"
           style={{backgroundImage:`url(${img1})`,backgroundAttachment:'fixed',
          backgroundSize:'cover',textAlign:'center', maxHeight:'800px', position:'relative'}} >
            <div className="row " style={{height: '99%'}} >         
               <div className="col-md-1" id="contour" 
             style={{float:"right" }}
             ></div>
         
          <div  className="  col-sm-12"   >
      <RBCarousel 
            
            className="carousel-fade roott  container-fluid     "
            
              animation={true}
              autoplay={this.state.autoplay}
              slideshowSpeed={3000}
              defaultActiveIndex={0}
              leftIcon={this.state.leftIcon}
              rightIcon={this.state.rightIcon}
              onSelect={this._onSelect}
              ref={r => (this.slider = r)}
              version={4}
            >
          
                    
               
               

               { 
               clubs.map((club,i)=>(

<div className="row " 
id='clubparallax'
>
  <div className="col-md-6 col-sm-12"><img className="d-block w-100 images img-fluid"
  src={club.image}
/>
</div>
               <div className="col-md-6 clubtext col-sm-12" style={{textAlign:"center",
               
               fontFamily: 'Trocchi, serif',
                fontSize: '30px',
                 fontWeight: 'normal',
                 lineHeight: '48px',
                 verticalAlign:"center", 
                 paddingBottom:'5%',
                 paddingTop:'2%'}}> 
               
                {club.dsc}
               <br/> <br/>
               <button className="btn btn-outline-primary"
  >

   {club.title}

  </button>
 
               </div>
               <div className="content-container">
        <img className="slider__img" src={clubs[slideIndex]} />
        <button onClick={() => this.plusDivs(1)}>Next</button>
        <button onClick={() => this.plusDivs(-1)}>Previous</button>
      </div>

  </div>
            

             
            
            
         ))}
 

              
        
 </RBCarousel>
 

              
       
              
 </div>
        </div>
        </div>

  </React.Fragment>
)

  
 

    
}
}
export default ClubSlider;
