import gauche from '../assets/gauche.png';
import droite from '../assets/droite.png';
import img2 from '../assets/bright_insat.png';
import {getClubs} from "../datafolder/clubData";
import './styling.css';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import React,{Component, useState,useEffect} from "react";
import "./styling.css";
import "react-bootstrap-carousel/dist/react-bootstrap-carousel.css";


const styles = { height: 400, width: "100%" };
const icon_glass = <span className="fa fa-glass" />;
const icon_music = <span className="fa fa-music" />;

class ClubSlider extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        clubs:getClubs(),
      autoplay: true,
      clicked: false
    
    
    };
  }

 
  render() {
    let {clubs, slideIndex,clicked}=this.state;
    const length=clubs.length;
    var clubs_bloc =[];
    var i = 1;
    var interval = 3000;
   
   
   
    function testing1 (){
      if(i<2){ i =length-1}
       i=i-2;
       animating();
       clearInterval(animation);
       animation = setInterval(animating,interval);
     }
   
    function testing (){
     var bloc =[];
     animating();
     clearInterval(animation);
     animation = setInterval(animating,interval);
    }
   var animating = function(){
      if (i<length-3){
      clubs_bloc = clubs.slice(i, i+3);
      i = i+1}
      else{
        let d = 3-(length -i );
        clubs_bloc = clubs.slice (i, length) ;  
        i=0;
        clubs_bloc = clubs_bloc.concat(clubs.slice(i, d));
        i=d;
      }


      document.getElementById('club_1').style.background ='url('+clubs_bloc[0].image+')';
      document.getElementById('club_2').style.backgroundImage =`url(${clubs_bloc[1].image})`;
      document.getElementById('club_3').style.backgroundImage =`url(${clubs_bloc[2].image})`;

    }

   var animation = setInterval(animating,interval);

   
    

    return (

        <React.Fragment>     
         <div className="row"  style={{ height:'50vh', marginTop:'80px',marginBottom:'50px'}} id='sliders'>
         <div className='col-md-1 arrowIcon' ><img src={gauche}  onClick= {testing1}  /></div>
          <div className='col-md-3 slideCol' id='club_1' style={{backgroundImage:'url('+clubs[0].image+')'}} >
              <div className="overlay">
                <div className='overlaycontent'>
                </div>
              </div>
          </div>
          <div className='col-md-3 slideCol' id='club_2' style={{backgroundImage:'url('+clubs[1].image+')'}}>
            <div className="overlay">
                <div className='overlaycontent'>
                </div>
              </div>
            </div>
          <div className='col-md-3 slideCol' id='club_3' style={{backgroundImage:'url('+clubs[2].image+')'}}>
            <div className="overlay">
                <div className='overlaycontent'>
                </div>
              </div>
            </div>
          <div className='col-md-1 arrowIcon' ><img src={droite} onClick= {testing}/></div>
          </div>
         
                      
           
       
         
             
  </React.Fragment>
)

  
 

    
}
}
export default ClubSlider;
