import React, { Component , useEffect} from 'react';
import events from '../assets/event.png';
import img2 from '../assets/blured.png';
import network from  '../assets/worldd.png';
import Navbar from './navbar';
import Slides from './slides';
import eventIcon from '../assets/eventicon.png';
import users from '../assets/users.png';
import sponsors from '../assets/sponsors.png';
import Aos from 'aos';
import 'aos/dist/aos.css';
import CountUp from 'react-countup';
import TheCalendar from './theCalendar';
import ClubSlider from './clubSlider';
const parallax = document.getElementById('firstparallax');
const Welcome = () => {

    useEffect(()=>{
        Aos.init({duration:4000});
    },[]);


    return (
        <React.Fragment>
      
        <Navbar className="ab"  style={{position: 'absolute'}}/>
 
    
          
          
<Slides className="root sliderr" style=
{{ position: 'relative',minHeight: '100vh' }} />
    
   
      
      
    

      <section id='about'  > 
           

        
          
          <div  className="row"style={{ paddingRight:'0',
            backgroundColor:'white',  paddingTop:"10%", margin:0
           }} >
          
          
           <div 
              
              className="aboutsection col-md-7" style={{paddingLeft:'4%'}} >
                <h1 style={{ paddingRight:'4%'}}>
                Bright INSAT, at a glance ..
                </h1>
                <p  >
                A communication bridge between INSAT clubs and students. 
                 <br/> We aim to help clubs expose their events easily to students, who won't feel sad about skipping 
                 an event that interest them anymore !
                </p>
 
            </div>
          
          <div className='col-md-5' style={{textAlign:'center'}}>
              <img src={events} style={{maxHeight:'50vh', padding:'10%'}}></img>
          </div>

          </div>
              
              
              

              
      
           
           <div className="row cell" style={{padding:'4rem 2rem', backgroundColor:'#545454', minHeight:'550px'}} >
               <div className='col-md-3 cells'>
               <span className="input-group-addon">
                   <img src={users}></img>
                   </span>

                   <h1>
                   
               <CountUp end={4500} />
               </h1>
               
               <h6 style={{textAlign:'center !important'}}>users</h6>
               </div>
               <div className='col-md-3 cells'>
               <span className="input-group-addon">
                   <img src={eventIcon}></img>
                   </span>

                   <h1>
                   
               <CountUp end={8000} />
               </h1>
               
               <h6 style={{textAlign:'center !important'}}>our events</h6>
               </div>
               <div className='col-md-3 cells'>
               <span className="input-group-addon">
                   <img src={sponsors}></img>
                   </span>

                   <h1>
                   
               <CountUp end={2000} />
               </h1>
               
               <h4 style={{textAlign:'center !important'}}>Partnership</h4>
               </div>
          

           </div>
           <div style={{padding:'0', margin:'0'}} >
           
           <div className="parallax" id='firstparallax' style={{backgroundImage:`url(${img2})`,backgroundAttachment:'fixed',
            backgroundSize:'cover',textAlign:'center', maxHeight:'100vh', position:'relative'}}>
                 <div  id='layer'  className='jumbotron calendar' >
   <TheCalendar />
   </div></div>

           

   
           </div>
           </section> 
      </React.Fragment>
      );
}
 
export default Welcome;
