import gauche from '../assets/gauche.png';
import droite from '../assets/droite.png';
import img2 from '../assets/bright_insat.png';
import {getClubs} from "../datafolder/clubData";
import {getEvents} from "../eventData";
import './styling.css';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import React,{Component, useState,useEffect} from "react";
import "./styling.css";
import "react-bootstrap-carousel/dist/react-bootstrap-carousel.css";
import Slider from '../common/slider';

const styles = { height: 400, width: "100%" };
const icon_glass = <span className="fa fa-glass" />;
const icon_music = <span className="fa fa-music" />;

class ClubSlider extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        clubs:getClubs(),
        events: getEvents(),
      autoplay: true,
      clicked: false
    
    
    };
  }

 
  render() {
    let {clubs, events}=this.state;
    const length=clubs.length;
    var clubs_bloc =clubs.slice(1, 3);
    var i = 1;
    var interval = 3000;
   
   
   
    function previousSlide (){
      if(i<2){ i =length-1}
       i=i-2;
       sliding();
       clearInterval(animation);
       animation = setInterval(sliding,interval);
     }
   
    function nextSlide (){
     sliding();
     clearInterval(animation);
     animation = setInterval(sliding,interval);
    }


   var sliding = function(){
      if (i<length-3){
      clubs_bloc = clubs.slice(i, i+3);
      i = i+1}
      else{
        let d = 3-(length -i );
        clubs_bloc = clubs.slice (i, length) ;  
        i=0;
        clubs_bloc = clubs_bloc.concat(clubs.slice(i, d));
        i=d;
      }


      document.getElementById('club_1').style.background =`url(${clubs_bloc[0].image})`;
      document.getElementById('club_2').style.backgroundImage =`url(${clubs_bloc[1].image})`;
      document.getElementById('club_3').style.backgroundImage =`url(${clubs_bloc[2].image})`;
      document.getElementById('first_btn').innerHTML = `${clubs_bloc[0].title}`;
      document.getElementById('sec_btn').innerHTML = `${clubs_bloc[1].title}`;
      document.getElementById('third_btn').innerHTML = `${clubs_bloc[2].title}`;

    }

   var animation = setInterval(sliding,interval);

   
    

    return (

        <React.Fragment>     
   
       
         <Slider list={clubs}/>
         
             
  </React.Fragment>
)

  
 

    
}
}
export default ClubSlider;
