    this.setState({ open: true });
  };
 
  onCloseModal = () => {
    this.setState({ open: false });
  };
 
   return (  <div>
    <button onClick={this.onOpenModal}>Open modal</button>
    <Modal open={open} onClose={this.onCloseModal} center>
      <h2>Simple centered modal</h2>
    </Modal>
  </div> );
 }
  
