import React, { Component , useEffect} from 'react';
import events from '../assets/event.png';
import img2 from '../assets/blured.png';
import _1 from '../assets/1.jpg';
import _2 from '../assets/2.jpg';
import _3 from '../assets/3.jpg';
import _4 from '../assets/5.jpg';

import network from  '../assets/worldd.png';
import Navbar from './navbar';
import Slides from './slides';
import eventIcon from '../assets/eventicon.png';
import users from '../assets/users.png';
import sponsors from '../assets/sponsors.png';
import Aos from 'aos';
import 'aos/dist/aos.css';
import CountUp from 'react-countup';
import Slider from 'react-slick';
import TheCalendar from './theCalendar';
import ClubSlider from './clubSlider';
const parallax = document.getElementById('firstparallax');
const Welcome = () => {

    useEffect(()=>{
        Aos.init({duration:4000});
    },[]);
var images=[_1,_2,_3,_4];

var settings={
    autoplay: false,
    className: "slider variable-width",
    speed: 2000,
    autoplaySpeed: 2000,
    infinite: true,
    cssEase: "linear",
    dots:false,
    arrows: true,
    speed:300,
    slidesToShow:4,
    slidesToScroll:3,
    responsive: [
    
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 2,
          initialSlide: 2
        }
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1
        }
      }
    ]
  }


    return (
        <React.Fragment>
      
        <Navbar className="ab"  style={{position: 'absolute'}}/>
 
    
          
          
<Slides className="root sliderr" style=
{{ position: 'relative',minHeight: '100vh' }} />
    
   
      
      
    

      <section id='about'  > 
           

        
          
          <div  className="row"style={{ paddingRight:'0',
            backgroundColor:'white',  paddingTop:"10%", margin:0
           }} >
          
          
           <div 
              
              className="aboutsection col-md-7" style={{paddingLeft:'4%'}} >
                <h1 style={{ paddingRight:'4%'}}>
                Bright INSAT, at a glance ..
                </h1>
                <p  >
                A communication bridge between INSAT clubs and students. 
                 <br/> We aim to help clubs expose their events easily to students, who won't feel sad about skipping 
                 an event that interest them anymore !
                </p>
 
            </div>
          
          <div className='col-md-5' style={{textAlign:'center'}}>
              <img src={events} style={{maxHeight:'60vh', paddingBottom:'20%'}}></img>
          </div>
          <hr style={{width:'50%'}}/>

          </div>
              
              
              
          <div className='row ' style={{backgroundColor:'white', height:'50vh'}}>
         
<div className='col-md-4 service'>
<div className='content'>
<span className="input-group-addon">
                   <img src={users}></img>
                   </span>
  <h3>see</h3>
  <p>in bright insat you will explore a lot </p>
</div>
</div>
<div className='col-md-4 service'>
<div className='content'>
<span className="input-group-addon">
                   <img src={eventIcon}></img>
                   </span>
  <h3>see</h3>
  <p>in bright insat you will explore a lot </p>
</div>
</div>
<div className='col-md-4 service'>
<div className='content'>
<span className="input-group-addon">
                   <img src={sponsors}></img>
                   </span>
                   <CountUp end={4500} />
  <h3>see</h3>
  <p>in bright insat you will explore a lot </p>
</div>
</div>
</div>
           
           
           <div className='row'style={{padding:'0', margin:'0', backgroundColor:'white'}} >
           
           <div className="parallax" id='firstparallax' style={{backgroundImage:`url(${img2})`,backgroundAttachment:'fixed',
            backgroundSize:'cover',textAlign:'center', maxHeight:'80vh', position:'relative'}}>
                  <Slider {...settings}>
                 {images.map(function(current){
                        
          return(

              
            <div   style={{objectFit:'cover !important', minHeight:'100%'}} >
              <img src= {current}  style={{objectFit:'cover !important'}}/>
             
            </div>

            )})}
       </Slider>
   </div>

           

   
           </div>
           </section> 
      </React.Fragment>
      );
}
 
export default Welcome;
