import React, { Component } from 'react';
import img1 from '../assets/cstc.png';
import img2 from '../assets/back.png';
import img3 from "../assets/cap.png";
import ReactBootstrapCarousel from "react-bootstrap-carousel";
import "bootstrap/dist/css/bootstrap.css";
import "react-bootstrap-carousel/dist/react-bootstrap-carousel.css";
import "./styling.css";
import {Link} from 'react-router-dom';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import Slider from "react-slick";
import {HomeWrapper } from './style';
import {getCategories} from "../categoriesData";
import {getEvents} from "../eventData";
import AOS from 'aos';

import 'aos/dist/aos.css';
//color #f4f0c0

import {
    Container,
    Row,
    Col,
    Card,
    CardBody,
    CardFooter,
    Badge,
    Button
  } from "shards-react";
import CategoriesList from '../common/categoriesList'
import Login from './login';
import Navbar from './navbar';
import SearchPage from './searchpage';
import Slides from './slides';
import ClubParent from './clubParent';
class EventsParent
 extends Component {
    state = { 
        events:[],
        categories:[],
       // filtered:'',
        selectedCategorie:''
    

     }

     
   
      componentWillReceiveProps (){ 
        AOS.refresh(); 
      } 
     componentDidMount(){
        this.setState({events: getEvents(), categories: getCategories()});
        AOS.init({
            duration : 2000
          })
    }
 
    handleCategorieSelect=categorie=>{
        //const{selectedCategorie,events,filtered}=this.state;
    
        this.setState({selectedCategorie: categorie});
       /* filtered= selectedCategorie
        ? events.filter(m=>m.categorie._id=== selectedCategorie._id)
        : events;
        */
        
        
    }
   
    render() { 
        const{selectedCategorie,events}=this.state;
        
        const filtered= selectedCategorie
        ? events.filter(m=>m.categorie._id=== selectedCategorie._id)
        : events;
        const l=filtered.length;
        const nb=l/5;
        let i=0;
        var settings={
          dots:false,
          arrows: true,
          infinite:false,
          speed:500,
          slidesToShow:3,
          slidesToScroll:3
        }

        return ( 
        <React.Fragment>



       <div 
       data-aos="fade-up" className="jumbotron j2" id="the" style={{backgroundColor:'#f4f0c0 !important'}} >
           
           <svg  id="curve"xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1440 320">
  <path fill="white" fill-opacity="1" d="M0,192L48,192C96,192,192,192,288,208C384,224,480,256,576,234.7C672,213,768,139,864,122.7C960,107,1056,149,1152,165.3C1248,181,1344,171,1392,165.3L1440,160L1440,320L1392,320C1344,320,1248,320,1152,320C1056,320,960,320,864,320C768,320,672,320,576,320C480,320,384,320,288,320C192,320,96,320,48,320L0,320Z"></path>
</svg>
                       <div className ="jumbotron j2"id="eventsection"
                        >
                            

     <h1 id="withCarousel" style={{marginTop:'0%', height:'10%'}}>TOUS NOS CATEGORIES</h1>

     
    <div      data-aos="fade-up"
    data-aos-offset="200"
    data-aos-delay="50"
    data-aos-duration="1000"
    data-aos-easing="ease-in-out"
    data-aos-mirror="true"
    data-aos-once="false"
    data-aos-anchor-placement="top-center">
          <CategoriesList
          style={{height:'20%'}}
            items={this.state.categories} 
            onItemSelect={this.handleCategorieSelect}
            selectedItem={this.state.selectedCategorie}
           />
           </div>

         <div className="j2">         <Container>
         <Link to='/'>see all</Link>
          {
            filtered.length===0?(
              <div className='spinner-border' role='status'>
                <span className='sr-only'>Loading..</span>
              </div>
            ):(
              
              <Slider {...settings}>
                 {filtered.map(function(current){
                        
          return(
            <HomeWrapper>
              
             
<div className="card-body">
              <div className="card h-100 text-center shadow card bm--card-equal-height" style={{height:'100% !important'}}> 
                
                    <img src={current.image} alt="img1" className="card-img-top"/>

                
                <div className="card-body text-dark flex-fill">
                 <h4 className='card-title' > {current.title}</h4>
                    <p className="card-text text-secondary">
                    
                    </p>
          <a href="#" className="btn btn-outline-success" > Learn More </a>
                
                
                </div>

            </div>
            </div>
           
              
             
            </HomeWrapper>
          );
          
          
          })}
              </Slider>
            )
          }



</Container> </div>
</div>
</div>
            </React.Fragment>);
    }
}
 
export default EventsParent
;
